            <tr>
                <th>Id</th>
                <th>Title</th>
                <th>Slug</th>
                <th>Content</th>
                <th>PublicationDate</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for post in posts %}
            <tr>
                <td>{{ post.id }}</td>
                <td>{{ post.title }}</td>
                <td>{{ post.slug }}</td>
                <td>{{ post.content }}</td>
                <td>{{ post.publicationDate ? post.publicationDate|date('d/m/Y H:i:s') : '' }}</td>
                <td>
                    <a href=\"{{ path('post_show', {'slug': post.slug}) }}\">show</a>
                    <a href=\"{{ path('post_edit', {'slug': post.slug}) }}\">edit</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"6\">no records found</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('post_new') }}\">Create new</a>
{% endblock %}
